--CREATE VIEW enty.vw_countries AS SELECT config_id as country_id,config_code as Country_code,config_value as Country_name FROM enty.tb_config WHERE configtype_id=2000001;

--CREATE VIEW enty.vw_state_provinces AS SELECT add.country_id,add.state_id,st.config_code as state_code,st.config_value as state_name FROM enty.tb_config st inner join enty.tb_address_map add ON add.state_id =st.config_id WHERE configtype_id=2000002;

CREATE VIEW enty.vw_countries AS SELECT config_id as country_id,config_code as Country_code,config_value as Country_name,configtype_code FROM enty.tb_config cf INNER JOIN enty.tb_configtype ct ON cf.configtype_id=ct.configtype_id WHERE ct.configtype_id=2000001;

CREATE VIEW enty.vw_state_provinces AS SELECT add.country_id,add.state_id,st.config_code as state_code,st.config_value as state_name,configtype_code FROM enty.tb_config st inner join enty.tb_address_map add ON add.state_id =st.config_id INNER JOIN enty.tb_configtype ct ON ct.configtype_id=st.configtype_id WHERE ct.configtype_id=2000002;

--CREATE VIEW enty.vw_config AS SELECT config_id as config_id,config_code, config_value as config_value,configtype_code FROM enty.tb_config cf INNER JOIN enty.tb_configtype ct ON cf.configtype_id=ct.configtype_id WHERE ct.configtype_id NOT IN(2000001,2000002);

CREATE VIEW enty.vw_config AS SELECT config_id as config_id,config_code,config_value as config_value,ct.configtype_id,configtype_code FROM enty.tb_config cf INNER JOIN enty.tb_configtype ct ON cf.configtype_id=ct.configtype_id WHERE ct.configtype_id NOT IN(2000001,2000002);

